import React, { useState } from "react";
import Map from "./Map";

const useSelectArea = () => {
  const [selectedArea, setSelectedArea] = useState('Littleroot Town');

  const changeSelectedArea = (e: any) => {
    setSelectedArea(e.target.title)
  }

  return {
    selectedArea,
    changeSelectedArea
  }
}

export default function Game() {
    const selectedAre = useSelectArea();

    return (
    <React.Fragment>
      <div style={
      {"position": "absolute",
      "left": "50%",
      "top": "30%",
      "transform": "translate(-50%, -50%)",
      }} > 
        <Map />
      <p>{selectedArea}</p>
      <button> Start </button>

      </div>

    </React.Fragment>
  )

}
