import React, { useState } from "react";
import Map from "./Map";
import hoenn from "./pokearth/hoenn.png";

const useSelectArea = () => {
  const [selectedArea, setSelectedArea] = useState('Littleroot Town');

  const changeSelectedArea = (e: string) => {
    setSelectedArea(e)
  }

  return {
    selectedArea ,
    changeSelectedArea
  }
}

export default function Game() {
    const { selectedArea, changeSelectedArea } = useSelectArea();

    return (
    <React.Fragment>
      <div style={
      {"position": "absolute",
      "left": "50%",
      "top": "30%",
      "transform": "translate(-50%, -50%)",
      }} > 
        <Map changeSelectedArea={changeSelectedArea} />

        <p>{selectedArea}</p>
        <button> Start </button>


      </div>

      <div style={
      {"position": "absolute",
      "left": "50%",
      "top": "70%",
      "transform": "translate(-50%, -50%)",
      }} > 

      <img src={hoenn} />

      </div>



    </React.Fragment>
  )

}
