import React, { useState } from "react";
import GameMap from "./GameMap";

const useSelectArea = () => {
  const [selectedArea, setSelectedArea] = useState('Littleroot Town');

  const changeSelectedArea = (e: string) => {
    setSelectedArea(e)
  }

  return {
    selectedArea ,
    changeSelectedArea
  }
}


export default function Game() {
    const { selectedArea, changeSelectedArea } = useSelectArea();
    const [isActive, setIsActive] = useState(false);
    const [image, setImage] = useState('Littleroot Town');

    function importAll(r: any) {
      let images: any = {};
      r.keys().forEach((item: any) => {
        images[item.replace('./', '')] = r(item);
      });
     return images;
    }

    const images = importAll(require.context("../../assets", false, /\.(png|jpe?g|svg)$/));

    function setRandomImage() {
      setIsActive(true);
      setImage(getRandomImage(images));
    }

    function getRandomImage(set: Map<string, any>): string {
      set = new Map(Object.entries(set))
      const items = Array.from(set.keys())
      return items[Math.floor(Math.random() * items.length)];
    }

    function guess() {
      const answer = image.slice(0, -4)
      console.log(answer)
      console.log(selectedArea)
      if (answer === selectedArea) {
        console.log("Right!");
      } else {
        console.log("Wrong!");
      }

      setIsActive(false);
    }

    return (
    <React.Fragment>
      <div style={
      {"position": "absolute",
      "left": "50%",
      "top": "30%",
      "transform": "translate(-50%, -50%)",
      }} > 

        <img src={images[image]} />

      </div>

      <div style={
      {"position": "absolute",
      "left": "50%",
      "top": "70%",
      "transform": "translate(-50%, -50%)",
      }} > 
        <GameMap changeSelectedArea={changeSelectedArea} />

        <p>{selectedArea}</p>

        {isActive ?
        (<button onClick={guess}  > Guess! </button>) :
        (<button onClick={setRandomImage}> Start </button>)
        }


      </div>
    </React.Fragment>
  )

}
