import React, { useState } from "react";
import Map from "./Map";
import hoenn from "./pokearth/hoenn.png";

const useSelectArea = () => {
  const [selectedArea, setSelectedArea] = useState('Littleroot Town');

  const changeSelectedArea = (e: string) => {
    setSelectedArea(e)
  }

  return {
    selectedArea ,
    changeSelectedArea
  }
}


export default function Game() {
    const { selectedArea, changeSelectedArea } = useSelectArea();

    function importAll(r: any) {
      let images: any = {};
      r.keys().forEach((item: any) => {
        images[item.replace('./', '')] = r(item);
      });
     return images;
    }

    const images = importAll(require.context("../../assets", false, /\.(png|jpe?g|svg)$/));

    function getRandomImage() {
      const items = Array.from(images);
      return items[Math.floor(Math.random() * items.length)];
    }

    return (
    <React.Fragment>
      <div style={
      {"position": "absolute",
      "left": "50%",
      "top": "30%",
      "transform": "translate(-50%, -50%)",
      }} > 

        <img src={images['Route 101.jpg']} />

      </div>

      <div style={
      {"position": "absolute",
      "left": "50%",
      "top": "70%",
      "transform": "translate(-50%, -50%)",
      }} > 
        <Map changeSelectedArea={changeSelectedArea} />

        <p>{selectedArea}</p>
        <button> Start </button>


      </div>
    </React.Fragment>
  )

}
